import React, { useState } from 'react';
import Navbar from './NavBar'; // Ensure the import path is correct
import '../styles/Featurepage.css'; // Use this CSS file for both features
import axios from 'axios';

const AudioDeepfake = () => {
  const [file, setFile] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!file) {
      setError('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/predict', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setResult(response.data);
    } catch (error) {
      setError('An error occurred while processing the file.');
      console.error(error);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="feature-content">
        <h1>Audio Deepfake Detection</h1>
        <p>Our audio deepfake detection tool helps in identifying manipulated audio clips. Protect your integrity with our cutting-edge technology.</p>
        <form onSubmit={handleSubmit}>
          <input type="file" onChange={handleFileChange} />
          <button type="submit">Upload</button>
        </form>
        {error && <p>{error}</p>}
        {result && (
          <div>
            <h2>Detection Result:</h2>
            <pre>{JSON.stringify(result, null, 2)}</pre>
          </div>
        )}
      </div>
    </div>
  );
};

export default AudioDeepfake;
